import React, { useState, useEffect } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import useSound from 'use-sound'

// Loader component
const Loader = () => (
  <div className="fixed inset-0 flex items-center justify-center bg-pink-100">
    <div className="w-16 h-16 border-4 border-blue-400 border-t-transparent rounded-full animate-spin"></div>
  </div>
)

// Confetti component
const Confetti = ({ index }) => (
  <motion.div
    className="absolute w-2 h-2 rounded-sm"
    initial={{ opacity: 0, scale: 0 }}
    animate={{
      opacity: [0, 1, 1, 0],
      scale: [0, 1, 1, 0],
      x: Math.random() * 200 - 100,
      y: Math.random() * -200 - 50,
      rotate: Math.random() * 360,
    }}
    transition={{ duration: 1.5, ease: "easeOut" }}
    style={{
      left: `${Math.random() * 100}%`,
      top: `${Math.random() * 100}%`,
      backgroundColor: ['#FF69B4', '#FFB6C1', '#FFA07A', '#98FB98', '#87CEFA', '#DDA0DD'][index % 6],
    }}
  />
)

// Main App component
const App: React.FC = () => {
  const [isLoading, setIsLoading] = useState(true)
  const [month, setMonth] = useState(0)
  const [taps, setTaps] = useState(0)
  const [confetti, setConfetti] = useState<number[]>([])

  const babyImages = [
    '/placeholder.svg?height=300&width=300',
    '/placeholder.svg?height=300&width=300',
    '/placeholder.svg?height=300&width=300',
    '/placeholder.svg?height=300&width=300',
    '/placeholder.svg?height=300&width=300',
    '/placeholder.svg?height=300&width=300',
    '/placeholder.svg?height=300&width=300',
    '/placeholder.svg?height=300&width=300',
    '/placeholder.svg?height=300&width=300',
    '/placeholder.svg?height=300&width=300',
    '/placeholder.svg?height=300&width=300',
    '/placeholder.svg?height=300&width=300',
  ]

  const babySounds = [
    '/baby-sound-1.mp3',
    '/baby-sound-2.mp3',
    '/baby-sound-3.mp3',
    '/baby-sound-4.mp3',
    '/baby-sound-5.mp3',
    '/baby-sound-6.mp3',
    '/baby-sound-7.mp3',
    '/baby-sound-8.mp3',
    '/baby-sound-9.mp3',
    '/baby-sound-10.mp3',
    '/baby-sound-11.mp3',
    '/baby-sound-12.mp3',
  ]

  const [playSound] = useSound(babySounds[month], { volume: 0.5 })

  useEffect(() => {
    const loadAssets = async () => {
      const imagePromises = babyImages.map(src => {
        return new Promise((resolve, reject) => {
          const img = new Image()
          img.src = src
          img.onload = resolve
          img.onerror = reject
        })
      })

      const soundPromises = babySounds.map(src => {
        return new Promise((resolve, reject) => {
          const audio = new Audio()
          audio.src = src
          audio.oncanplaythrough = resolve
          audio.onerror = reject
        })
      })

      try {
        await Promise.all([...imagePromises, ...soundPromises])
        setIsLoading(false)
      } catch (error) {
        console.error('Failed to load assets:', error)
        setIsLoading(false) // Proceed even if some assets fail to load
      }
    }

    loadAssets()
  }, [])

  useEffect(() => {
    if (taps >= 10) {
      setMonth((prevMonth) => (prevMonth + 1) % 12)
      setTaps(0)
      setConfetti([])
    }
  }, [taps])

  const handleTap = () => {
    setTaps((prevTaps) => prevTaps + 1)
    setConfetti((prevConfetti) => [...prevConfetti, ...Array(10).fill(0).map(() => Date.now() + Math.random())])
    playSound()
  }

  if (isLoading) {
    return <Loader />
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-pink-100 to-blue-100 flex flex-col items-center justify-center p-4">
      <h1 className="text-3xl font-bold mb-8 text-center text-gray-800">
        Baby's First Year
      </h1>
      <div className="relative w-full max-w-sm aspect-square">
        <div className="absolute inset-0 flex items-center justify-center">
          <motion.div
            className="w-full h-full bg-pink-200 rounded-full"
            initial={{ scale: 0 }}
            animate={{ scale: taps / 10 }}
            transition={{ duration: 0.2 }}
          />
        </div>
        <div className="relative w-full h-full rounded-full overflow-hidden shadow-xl">
          <AnimatePresence mode="wait">
            <motion.img
              key={month}
              src={babyImages[month]}
              alt={`Baby at ${month + 1} month${month !== 0 ? 's' : ''}`}
              className="w-full h-full object-cover"
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 1.2 }}
              transition={{ duration: 0.5 }}
              onClick={handleTap}
            />
          </AnimatePresence>
        </div>
        <div className="absolute inset-0 pointer-events-none overflow-hidden">
          <AnimatePresence>
            {confetti.map((id, index) => (
              <Confetti key={id} index={index} />
            ))}
          </AnimatePresence>
        </div>
      </div>
      <p className="mt-8 text-xl font-semibold text-gray-700">
        {month + 1} month{month !== 0 ? 's' : ''} old
      </p>
      <p className="mt-2 text-sm text-gray-600">
        Tap the image {10 - taps} more time{taps !== 9 ? 's' : ''} to see the next month!
      </p>
    </div>
  )
}

export default App
